import { Component } from '@angular/core';

@Component({
  selector: 'app-component-animal',
  templateUrl: './component-animal.component.html',
  styleUrls: ['./component-animal.component.css']
})

export class AppComponent {
  newContact = {
    name: '',
    species: '',
    age: 0
  };
  contacts = [];

  addContact() {
    this.contacts.push({ ...this.newContact });
    this.newContact = {
      name: '',
      species: '',
      age: 0
    };
  }
}

export class ComponentAnimalComponent {

}
<h1>Répertoire des Animaux de Compagnie</h1>
<div class="container">
  <div class="row">
    <div class="col-md-6">
      <h2>Ajouter un Animal de Compagnie</h2>
      <form (ngSubmit)="addContact()" #contactForm="ngForm">
        <div class="form-group">
          <label for="name">Nom</label>
          <input type="text" id="name" name="name" [(ngModel)]="newContact.name" class="form-control" required>
        </div>
        <div class="form-group">
          <label for="species">Espèce</label>
          <input type="text" id="species" name="species" [(ngModel)]="newContact.species" class="form-control" required>
        </div>
        <div class="form-group">
          <label for="age">Âge</label>
          <input type="number" id="age" name="age" [(ngModel)]="newContact.age" class="form-control" required>
        </div>
        <button type="submit" class="btn btn-primary">Ajouter</button>
      </form>
    </div>
    <div class="col-md-6">
      <h2>Liste des Animaux de Compagnie</h2>
      <ul>
        <li *ngFor="let contact of contacts">
          {{ contact.name }} ({{ contact.species }}), âgé de {{ contact.age }} ans
        </li>
      </ul>
    </div>
  </div>
</div>
