export class AppComponent {
  newContact = {
    name: '',
    species: '',
    age: 0
  };
  contacts = [];

  addContact() {
    this.contacts.push({ ...this.newContact });
    this.newContact = {
      name: '',
      species: '',
      age: 0
    };
  }
}
