import { Component, OnInit } from '@angular/core';

interface Personne {
  nom: string;
  prenom
  age: number;
}

@Component({
  selector: 'app-gestion-personne',
  templateUrl: './ajout-personne.component.html',
  styleUrls: ['./ajout-personne.component.css']
})

export class GestionPersonneComponent implements OnInit {
  personnes: Personne[] = [];

  ajouterPersonne(personne: Personne): void {
    this.personnes.push(personne);
  }

  ngOnInit(): void {
  }
}
<h2>Ajouter une Personne</h2>
<form (ngSubmit)="ajouterPersonne()">
  <div>
    <label for="nom">Nom:</label>
    <input type="text" id="nom" [(ngModel)]="nouvellePersonne.nom" name="nom" required>
  </div>
  <div>
    <label for="prenom">Prenom:</label>
    <input type="text" id="prenom" [(ngModel)]="nouvellePersonne.prenom" name="prenom" required>
  </div>
  <div>
    <label for="age">Âge:</label>
    <input type="number" id="age" [(ngModel)]="nouvellePersonne.age" name="age" required>
  </div>
  <button type="submit">Ajouter</button>
</form>
