import { Component } from '@angular/core';

@Component({
  selector: 'app-gestion-personne',
  templateUrl: './gestion-personne.component.html',
  styleUrls: ['./gestion-personne.component.css']
})


export class GestionPersonneComponent implements OnInit {

    personnes: Array<{ nom: string, age: number }> = [];

    ajouterPersonne(personne: { nom: string, age: number }) {
      this.personnes.push(personne);
    }

    ngOnInit() {}
  }

<p>gestion-personn<h2>Liste de Personnes</h2>
    <table>
      <thead>
        <tr>
          <th>Nom</th>
          <th>Âge</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let personne of personnes">
          <td>{{ personne.nom }}</td>
          <td>{{ personne.age }}</td>
        </tr>
      </tbody>
    </table>
<p>the works!</p>
