  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title: string = 'My First Angular Project';

  constructor() {
    // Vous pouvez initialiser des variables ou effectuer d'autres tâches ici.
  }
}
<h1>Exemple d'utilisation du pipe de formatage de texte</h1>

